import { Icon } from "../../../../components";
import { SpecialPanel } from '../special-panel/special-panel';
import styled from "styled-components";


const PostContentContainer = ({ 
    className, 
    post:   {id, title, imageUrl, content, publishedAt, },

}) => {

    return (
        <div className={className}>
            <img src={imageUrl} alt={title} margin="-20px 0 20px" />
            <h2>{title}</h2>
            <SpecialPanel publishedAt={publishedAt} />
            <div className="special-panel">
                <div className="published-at">
                    <Icon id="fa-calendar-o" size="18px"  margin="0 7px 0 0" onClick={() => {}} />  
                    {publishedAt}  
                </div>
                <div className="buttons">
                    <Icon id="fa-pencil-square-o" size="21px"  margin="0 10px 0 0" onClick={() => {}} />  
                    <Icon id="fa-trash-o"  onClick={() => {}} />  
                </div>
            </div>
            <div className="post-text">{content}</div>

        </div>

    );

};

export const PostContent = styled(PostContentContainer)`
    & img {
        float:  left;
        margin: 0 20px 10px 0;
    } 

    & .post-text {
        font-size:  18px;
    }

`;
