export async function request(url, method, data) {
    console.log(`in request url =  ${url} method = ${method} `)
    console.log(data)

    return fetch(url, {
        headers: {
            'content-type': 'application/json'
        },
        method: method || 'GET',
        body:   data ? JSON.stringify(data) : undefined,
    }).then(res => res.json())
}
