import { useServerRequest } from "../../hooks";
import { Pagination, PostCard } from "./components";
import { useCallback, useEffect, useState } from "react";
import { PAGINATION_LIMIT } from "../../bff/constants";
import { debounce, getLastPageFromLinks } from "./utils";
import { Search } from "./components";
import styled from "styled-components";


const MainContainer = ({ className }) => {
    const [posts, setPosts] = useState([]);
    const [page, setPage] = useState(1);
    const [lastPage, setLastPage] = useState(1);
    const requestServer = useServerRequest();
    const [shouldSearch, setShouldSearch] = useState(false);
    const [searchPhrase, setSearchPhrase] = useState('');

    useEffect(() => {

            requestServer('fetchPosts', searchPhrase, page, PAGINATION_LIMIT).then(({ res: {posts, links }}) => {
                setPosts(posts);
                setLastPage(getLastPageFromLinks(links));
            });    
    }, [requestServer, page, shouldSearch]);

    const startDelayedSearch = useCallback((setShouldSearch) => debounce(setShouldSearch, 2000), []);;
    console.log(posts);
    console.log(`in Main lastPage = ${lastPage}`);

    const onSearch = ({ target}) => {
       setSearchPhrase(target.value) ;
       startDelayedSearch(!shouldSearch);
    }

    return (
        <div className={className}>
            <Search searchPhrase={searchPhrase} onChange={onSearch} />
            <div className="post-list">


                {posts.map(({ id, title, imageUrl, publishedAt, commentsCount }) => <PostCard 
                    key={id}
                    id={id}
                    title={title}
                    imageUrl={imageUrl}
                    publishedAt={publishedAt}
                    commentsCount={commentsCount}

                />)}
            </div>  
            {lastPage > 1 && (  
                 <Pagination page={page} lastPage={lastPage} setPage={setPage}/>
            )}
        </div>


    );


};

export const Main = styled(MainContainer) `
    & .post-list {
        display:    flex;
        flex-wrap:  wrap;
        padding:    20px;
    }
`;
