import { setPostData } from "./set-post-data";

export const removeCommentAsync = (requestServer, userId, postId, content) => (dispatch) => {
    requestServer('removePostComment', userId, postId, content).then((postData) => {
        dispatch(setPostData(postData.res));
    }) 
};
